import React, { useState, useEffect } from 'react';
import './TerminationForm.css';

function TerminationForm({ employee, onClose, onSuccess }) {
    const [formData, setFormData] = useState({
        employee_id: employee.employee_id,
        termination_type: 'Resignation',
        termination_date: '',
        last_working_day: '',
        reason: '',
        notice_period_days: '',
        severance_pay: '',
        unused_leave_payout: '',
        final_settlement: '',
        return_company_property: '',
        exit_interview_completed: false,
        exit_interview_notes: '',
        rehire_eligible: true,
        notes: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [workYears, setWorkYears] = useState(0);
    const [calculatedSeverance, setCalculatedSeverance] = useState(0);

    // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏≠‡∏≤‡∏¢‡∏∏‡∏á‡∏≤‡∏ô
    useEffect(() => {
        if (employee.hire_date && formData.termination_date) {
            const hireDate = new Date(employee.hire_date);
            const terminationDate = new Date(formData.termination_date);
            const diffTime = terminationDate - hireDate;
            const diffDays = diffTime / (1000 * 60 * 60 * 24);
            const years = diffDays / 365.25;
            setWorkYears(years);

            // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏Ñ‡πà‡∏≤‡∏ä‡∏î‡πÄ‡∏ä‡∏¢‡∏ï‡∏≤‡∏°‡∏°‡∏≤‡∏ï‡∏£‡∏≤ 118
            calculateSeverancePay(years);
        }
    }, [employee.hire_date, formData.termination_date]);

    // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏Ñ‡πà‡∏≤‡∏ä‡∏î‡πÄ‡∏ä‡∏¢‡∏ï‡∏≤‡∏°‡∏°‡∏≤‡∏ï‡∏£‡∏≤ 118 ‡∏û.‡∏£.‡∏ö. ‡∏Ñ‡∏∏‡πâ‡∏°‡∏Ñ‡∏£‡∏≠‡∏á‡πÅ‡∏£‡∏á‡∏á‡∏≤‡∏ô ‡∏û.‡∏®. 2541
    const calculateSeverancePay = (years) => {
        const monthlySalary = parseFloat(employee.salary) || 0;
        const dailyWage = monthlySalary / 30; // ‡∏Ñ‡πà‡∏≤‡∏à‡πâ‡∏≤‡∏á‡∏£‡∏≤‡∏¢‡∏ß‡∏±‡∏ô
        let days = 0; // ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏ß‡∏±‡∏ô‡∏Ç‡∏≠‡∏á‡∏Ñ‡πà‡∏≤‡∏à‡πâ‡∏≤‡∏á
        let severance = 0;

        if (years < 0.333) { // ‡∏ô‡πâ‡∏≠‡∏¢‡∏Å‡∏ß‡πà‡∏≤ 120 ‡∏ß‡∏±‡∏ô
            days = 0;
            severance = 0;
        } else if (years >= 0.333 && years < 1) { // 120 ‡∏ß‡∏±‡∏ô - < 1 ‡∏õ‡∏µ
            days = 30;
            severance = dailyWage * 30;
        } else if (years >= 1 && years < 3) { // 1 ‡∏õ‡∏µ - < 3 ‡∏õ‡∏µ
            days = 90;
            severance = dailyWage * 90;
        } else if (years >= 3 && years < 6) { // 3 ‡∏õ‡∏µ - < 6 ‡∏õ‡∏µ
            days = 180;
            severance = dailyWage * 180;
        } else if (years >= 6 && years < 10) { // 6 ‡∏õ‡∏µ - < 10 ‡∏õ‡∏µ
            days = 240;
            severance = dailyWage * 240;
        } else if (years >= 10 && years < 20) { // 10 ‡∏õ‡∏µ - < 20 ‡∏õ‡∏µ
            days = 300;
            severance = dailyWage * 300;
        } else if (years >= 20) { // 20 ‡∏õ‡∏µ‡∏Ç‡∏∂‡πâ‡∏ô‡πÑ‡∏õ
            days = 400;
            severance = dailyWage * 400;
        }

        setCalculatedSeverance(severance);

        // Auto-fill ‡∏Ñ‡πà‡∏≤‡∏ä‡∏î‡πÄ‡∏ä‡∏¢ (‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏Å‡∏≤‡∏£‡πÄ‡∏•‡∏¥‡∏Å‡∏à‡πâ‡∏≤‡∏á/‡πÑ‡∏•‡πà‡∏≠‡∏≠‡∏Å)
        if (formData.termination_type === 'Dismissal' || formData.termination_type === 'Layoff') {
            setFormData(prev => ({
                ...prev,
                severance_pay: severance.toFixed(2)
            }));
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));

        // ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Å‡∏≤‡∏£‡πÄ‡∏•‡∏¥‡∏Å‡∏à‡πâ‡∏≤‡∏á ‡πÉ‡∏´‡πâ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏Ñ‡πà‡∏≤‡∏ä‡∏î‡πÄ‡∏ä‡∏¢‡πÉ‡∏´‡∏°‡πà
        if (name === 'termination_type') {
            if (value === 'Dismissal' || value === 'Layoff') {
                setFormData(prev => ({
                    ...prev,
                    severance_pay: calculatedSeverance.toFixed(2)
                }));
            } else {
                // ‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏≠‡∏∑‡πà‡∏ô‡πÜ ‡∏≠‡∏≤‡∏à‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡∏Ñ‡πà‡∏≤‡∏ä‡∏î‡πÄ‡∏ä‡∏¢
                setFormData(prev => ({
                    ...prev,
                    severance_pay: '0'
                }));
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/terminations', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å');
            }

            onSuccess();
            onClose();
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content large" onClick={e => e.stopPropagation()}>
                <div className="modal-header">
                    <h2>‡πÅ‡∏ö‡∏ö‡∏ü‡∏≠‡∏£‡πå‡∏°‡πÄ‡∏•‡∏¥‡∏Å‡∏à‡πâ‡∏≤‡∏á / ‡∏™‡∏¥‡πâ‡∏ô‡∏™‡∏∏‡∏î‡∏Å‡∏≤‡∏£‡∏à‡πâ‡∏≤‡∏á</h2>
                    <button className="close-button" onClick={onClose}>&times;</button>
                </div>

                <form onSubmit={handleSubmit} className="termination-form">
                    {error && <div className="error-message">{error}</div>}

                    <div className="employee-info">
                        <h3>‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô</h3>
                        <p><strong>‡∏£‡∏´‡∏±‡∏™:</strong> {employee.employee_code}</p>
                        <p><strong>‡∏ä‡∏∑‡πà‡∏≠:</strong> {employee.title} {employee.first_name} {employee.last_name}</p>
                        <p><strong>‡πÅ‡∏ú‡∏ô‡∏Å:</strong> {employee.department_name}</p>
                        <p><strong>‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á:</strong> {employee.position_name}</p>
                    </div>

                    <div className="form-section">
                        <h3>‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏Å‡∏≤‡∏£‡πÄ‡∏•‡∏¥‡∏Å‡∏à‡πâ‡∏≤‡∏á</h3>

                        <div className="form-row">
                            <div className="form-group">
