import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { employeeAPI } from '../services/api';
import EmployeeForm from '../components/EmployeeForm';

function EmployeeList() {
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [editEmployee, setEditEmployee] = useState(null);

    useEffect(() => {
        fetchEmployees();
    }, []);

    const fetchEmployees = async () => {
        try {
            setLoading(true);
            const response = await employeeAPI.getAll();
            setEmployees(response.data);
            setError(null);
        } catch (err) {
            setError('ไม่สามารถโหลดข้อมูลพนักงานได้');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleAdd = () => {
        setEditEmployee(null);
        setShowModal(true);
    };

    const handleEdit = (employee) => {
        setEditEmployee(employee);
        setShowModal(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('คุณต้องการลบพนักงานนี้หรือไม่?')) {
            try {
                await employeeAPI.delete(id);
                fetchEmployees();
            } catch (err) {
                alert('ไม่สามารถลบพนักงานได้');
                console.error(err);
            }
        }
    };

    const handleFormSuccess = () => {
        setShowModal(false);
        fetchEmployees();
    };

    if (loading) return <div className="loading">กำลังโหลด...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
                <h2>จัดการข้อมูลพนักงาน</h2>
                <button className="btn" onClick={handleAdd}>
                    + เพิ่มพนักงาน
                </button>
            </div>

            <div className="table-container">
                <table>
                    <thead>
                        <tr>
                            <th>รหัสพนักงาน</th>
                            <th>ชื่อ-นามสกุล</th>
                            <th>แผนก</th>
                            <th>ตำแหน่ง</th>
                            <th>อีเมล</th>
                            <th>สถานะ</th>
                            <th>การดำเนินการ</th>
                        </tr>
                    </thead>
                    <tbody>
                        {employees.map(employee => (
                            <tr key={employee.employee_id}>
                                <td>{employee.employee_code}</td>
                                <td>
                                    <Link to={`/employees/${employee.employee_id}`}>
                                        {employee.title} {employee.first_name} {employee.last_name}
                                    </Link>
                                </td>
                                <td>{employee.department_name || '-'}</td>
                                <td>{employee.position_name || '-'}</td>
                                <td>{employee.email || '-'}</td>
                                <td>
                                    <span style={{
                                        padding: '0.25rem 0.75rem',
                                        borderRadius: '12px',
                                        fontSize: '0.85rem',
                                        backgroundColor: employee.employment_status === 'Active' ? '#d4edda' : '#f8d7da',
                                        color: employee.employment_status === 'Active' ? '#155724' : '#721c24'
                                    }}>
                                        {employee.employment_status}
                                    </span>
                                </td>
                                <td>
                                    <button className="btn btn-secondary" onClick={() => handleEdit(employee)}>
                                        แก้ไข
                                    </button>
                                    <button className="btn btn-danger" onClick={() => handleDelete(employee.employee_id)}>
                                        ลบ
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {showModal && (
                <EmployeeForm
                    employee={editEmployee}
                    onClose={() => setShowModal(false)}
                    onSuccess={handleFormSuccess}
                />
            )}
        </div>
    );
}

export default EmployeeList;
