import React, { useState, useEffect } from 'react';
import './TerminationForm.css';

function TerminationForm({ employee, onClose, onSuccess }) {
    const [formData, setFormData] = useState({
        employee_id: employee.employee_id,
        termination_type: 'Resignation',
        termination_date: '',
        last_working_day: '',
        reason: '',
        notice_period_days: '',
        severance_pay: '',
        unused_leave_payout: '',
        final_settlement: '',
        return_company_property: '',
        exit_interview_completed: false,
        exit_interview_notes: '',
        rehire_eligible: true,
        notes: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [workYears, setWorkYears] = useState(0);
    const [calculatedSeverance, setCalculatedSeverance] = useState(0);

    // คำนวณอายุงาน
    useEffect(() => {
        if (employee.hire_date && formData.termination_date) {
            // แปลงวันที่ให้ถูกต้อง (ใช้ local date แทน UTC)
            const hireDateStr = employee.hire_date.split('T')[0]; // ตัด time zone ออก
            const terminationDateStr = formData.termination_date;

            const hireDate = new Date(hireDateStr + 'T00:00:00');
            const terminationDate = new Date(terminationDateStr + 'T00:00:00');

            // คำนวณความแตกต่างของวัน
            const diffTime = terminationDate.getTime() - hireDate.getTime();
            const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
            const years = diffDays / 365.25;

            setWorkYears(years);

            // คำนวณค่าชดเชยตามมาตรา 118
            calculateSeverancePay(years);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [employee.hire_date, formData.termination_date]);

    // คำนวณค่าชดเชยตามมาตรา 118 พ.ร.บ. คุ้มครองแรงงาน พ.ศ. 2541
    const calculateSeverancePay = (years) => {
        const monthlySalary = parseFloat(employee.salary) || 0;
        const dailyWage = monthlySalary / 30; // ค่าจ้างรายวัน
        let days = 0; // จำนวนวันของค่าจ้าง
        let severance = 0;

        if (years < 0.333) { // น้อยกว่า 120 วัน
            days = 0;
            severance = 0;
        } else if (years >= 0.333 && years < 1) { // 120 วัน - < 1 ปี
            days = 30;
            severance = dailyWage * 30;
        } else if (years >= 1 && years < 3) { // 1 ปี - < 3 ปี
            days = 90;
            severance = dailyWage * 90;
        } else if (years >= 3 && years < 6) { // 3 ปี - < 6 ปี
            days = 180;
            severance = dailyWage * 180;
        } else if (years >= 6 && years < 10) { // 6 ปี - < 10 ปี
            days = 240;
            severance = dailyWage * 240;
        } else if (years >= 10 && years < 20) { // 10 ปี - < 20 ปี
            days = 300;
            severance = dailyWage * 300;
        } else if (years >= 20) { // 20 ปีขึ้นไป
            days = 400;
            severance = dailyWage * 400;
        }

        setCalculatedSeverance(severance);

        // Auto-fill ค่าชดเชย (เฉพาะถ้าเป็นการเลิกจ้าง/ไล่ออก)
        if (formData.termination_type === 'Dismissal' || formData.termination_type === 'Layoff') {
            setFormData(prev => ({
                ...prev,
                severance_pay: severance.toFixed(2)
            }));
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));

        // เมื่อเปลี่ยนประเภทการเลิกจ้าง ให้คำนวณค่าชดเชยใหม่
        if (name === 'termination_type') {
            if (value === 'Dismissal' || value === 'Layoff') {
                setFormData(prev => ({
                    ...prev,
                    severance_pay: calculatedSeverance.toFixed(2)
                }));
            } else {
                // ประเภทอื่นๆ อาจไม่ได้ค่าชดเชย
                setFormData(prev => ({
                    ...prev,
                    severance_pay: '0'
                }));
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/terminations', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'เกิดข้อผิดพลาดในการบันทึก');
            }

            onSuccess();
            onClose();
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content large" onClick={e => e.stopPropagation()}>
                <div className="modal-header">
                    <h2>แบบฟอร์มเลิกจ้าง / สิ้นสุดการจ้าง</h2>
                    <button className="close-button" onClick={onClose}>&times;</button>
                </div>

                <form onSubmit={handleSubmit} className="termination-form">
                    {error && <div className="error-message">{error}</div>}

                    <div className="employee-info">
                        <h3>ข้อมูลพนักงาน</h3>
                        <p><strong>รหัส:</strong> {employee.employee_code}</p>
                        <p><strong>ชื่อ:</strong> {employee.title} {employee.first_name} {employee.last_name}</p>
                        <p><strong>แผนก:</strong> {employee.department_name}</p>
                        <p><strong>ตำแหน่ง:</strong> {employee.position_name}</p>
                    </div>

                    <div className="form-section">
                        <h3>ข้อมูลการเลิกจ้าง</h3>

                        <div className="form-row">
                            <div className="form-group">
                                <label>ประเภทการเลิกจ้าง *</label>
                                <select
                                    name="termination_type"
                                    value={formData.termination_type}
                                    onChange={handleChange}
                                    required
                                >
                                    <option value="Resignation">ลาออก (Resignation)</option>
                                    <option value="Retirement">เกษียณอายุ (Retirement)</option>
                                    <option value="Dismissal">ไล่ออก (Dismissal)</option>
                                    <option value="Layoff">เลิกจ้าง (Layoff)</option>
                                    <option value="Contract_End">สิ้นสุดสัญญา (Contract End)</option>
                                    <option value="Mutual_Agreement">ตกลงร่วมกัน (Mutual Agreement)</option>
                                </select>
                            </div>

                            <div className="form-group">
                                <label>วันที่เลิกจ้าง *</label>
                                <input
                                    type="date"
                                    name="termination_date"
                                    value={formData.termination_date}
                                    onChange={handleChange}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>วันทำงานวันสุดท้าย *</label>
                                <input
                                    type="date"
                                    name="last_working_day"
                                    value={formData.last_working_day}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label>เหตุผล</label>
                            <textarea
                                name="reason"
                                value={formData.reason}
                                onChange={handleChange}
                                rows="3"
                                placeholder="ระบุเหตุผลการเลิกจ้าง..."
                            />
                        </div>
                    </div>

                    <div className="form-section">
                        <h3>การเงินและค่าชดเชย</h3>

                        {/* แสดงข้อมูลการคำนวณ */}
                        {formData.termination_date && (
                            <div className="calculation-info">
                                <div className="info-row">
                                    <span className="info-label">วันที่เริ่มงาน:</span>
                                    <span className="info-value">
                                        {new Date(employee.hire_date).toLocaleDateString('th-TH', {
                                            year: 'numeric',
                                            month: 'long',
                                            day: 'numeric'
                                        })}
                                    </span>
                                </div>
                                <div className="info-row">
                                    <span className="info-label">อายุงาน:</span>
                                    <span className="info-value highlight">
                                        {workYears.toFixed(2)} ปี ({Math.floor(workYears)} ปี {Math.floor((workYears % 1) * 12)} เดือน)
                                    </span>
                                </div>
                                <div className="info-row">
                                    <span className="info-label">เงินเดือนปัจจุบัน:</span>
                                    <span className="info-value">
                                        ฿{parseFloat(employee.salary).toLocaleString()}
                                    </span>
                                </div>
                                <div className="info-row calculation-result">
                                    <span className="info-label">ค่าชดเชยตามกฎหมาย (มาตรา 118):</span>
                                    <span className="info-value highlight">
                                        ฿{calculatedSeverance.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                                    </span>
                                </div>
                                <div className="law-reference">
                                    📋 <strong>อ้างอิง:</strong> พระราชบัญญัติคุ้มครองแรงงาน พ.ศ. 2541 มาตรา 118
                                    <br />
                                    <div style={{ marginTop: '0.5rem' }}>
                                        {workYears < 0.333 && "• น้อยกว่า 120 วัน: ไม่มีสิทธิ์ได้รับ"}
                                        {workYears >= 0.333 && workYears < 1 && "• 120 วัน - < 1 ปี: 30 วันของค่าจ้างสุดท้าย"}
                                        {workYears >= 1 && workYears < 3 && "• 1 ปี - < 3 ปี: 90 วันของค่าจ้างสุดท้าย"}
                                        {workYears >= 3 && workYears < 6 && "• 3 ปี - < 6 ปี: 180 วันของค่าจ้างสุดท้าย"}
                                        {workYears >= 6 && workYears < 10 && "• 6 ปี - < 10 ปี: 240 วันของค่าจ้างสุดท้าย"}
                                        {workYears >= 10 && workYears < 20 && "• 10 ปี - < 20 ปี: 300 วันของค่าจ้างสุดท้าย"}
                                        {workYears >= 20 && "• ≥ 20 ปี: 400 วันของค่าจ้างสุดท้าย"}
                                    </div>
                                    <div style={{ marginTop: '0.75rem', fontSize: '0.8rem', color: '#6c757d', fontStyle: 'italic' }}>
                                        * ค่าจ้างรายวัน = เงินเดือน ÷ 30 วัน
                                    </div>
                                </div>
                            </div>
                        )}

                        <div className="form-row">
                            <div className="form-group">
                                <label>ระยะเวลาแจ้งล่วงหน้า (วัน)</label>
                                <input
                                    type="number"
                                    name="notice_period_days"
                                    value={formData.notice_period_days}
                                    onChange={handleChange}
                                    min="0"
                                />
                            </div>

                            <div className="form-group">
                                <label>ค่าชดเชย (บาท)</label>
                                <input
                                    type="number"
                                    name="severance_pay"
                                    value={formData.severance_pay}
                                    onChange={handleChange}
                                    min="0"
                                    step="0.01"
                                />
                            </div>

                            <div className="form-group">
                                <label>ค่าจ้างวันลาคงเหลือ (บาท)</label>
                                <input
                                    type="number"
                                    name="unused_leave_payout"
                                    value={formData.unused_leave_payout}
                                    onChange={handleChange}
                                    min="0"
                                    step="0.01"
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label>ยอดชำระสุทธิ (บาท)</label>
                            <input
                                type="number"
                                name="final_settlement"
                                value={formData.final_settlement}
                                onChange={handleChange}
                                min="0"
                                step="0.01"
                            />
                        </div>
                    </div>

                    <div className="form-section">
                        <h3>การคืนทรัพย์สินและ Exit Interview</h3>

                        <div className="form-group">
                            <label>ทรัพย์สินบริษัทที่ต้องคืน</label>
                            <textarea
                                name="return_company_property"
                                value={formData.return_company_property}
                                onChange={handleChange}
                                rows="2"
                                placeholder="เช่น: โน้ตบุ๊ค, บัตรพนักงาน, กุญแจ..."
                            />
                        </div>

                        <div className="form-group checkbox-group">
                            <label>
                                <input
                                    type="checkbox"
                                    name="exit_interview_completed"
                                    checked={formData.exit_interview_completed}
                                    onChange={handleChange}
                                />
                                <span>ทำ Exit Interview แล้ว</span>
                            </label>
                        </div>

                        <div className="form-group">
                            <label>บันทึก Exit Interview</label>
                            <textarea
                                name="exit_interview_notes"
                                value={formData.exit_interview_notes}
                                onChange={handleChange}
                                rows="3"
                                placeholder="บันทึกจาก Exit Interview..."
                            />
                        </div>

                        <div className="form-group checkbox-group">
                            <label>
                                <input
                                    type="checkbox"
                                    name="rehire_eligible"
                                    checked={formData.rehire_eligible}
                                    onChange={handleChange}
                                />
                                <span>สามารถจ้างกลับได้ในอนาคต</span>
                            </label>
                        </div>

                        <div className="form-group">
                            <label>หมายเหตุเพิ่มเติม</label>
                            <textarea
                                name="notes"
                                value={formData.notes}
                                onChange={handleChange}
                                rows="3"
                                placeholder="หมายเหตุเพิ่มเติม..."
                            />
                        </div>
                    </div>

                    <div className="form-actions">
                        <button type="button" onClick={onClose} disabled={loading}>
                            ยกเลิก
                        </button>
                        <button type="submit" className="primary" disabled={loading}>
                            {loading ? 'กำลังบันทึก...' : 'บันทึกการเลิกจ้าง'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default TerminationForm;
