import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { employeeAPI } from '../services/api';

function EmployeeDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [employee, setEmployee] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState('personal');

    useEffect(() => {
        fetchEmployee();
    }, [id]);

    const fetchEmployee = async () => {
        try {
            setLoading(true);
            const response = await employeeAPI.getById(id);
            setEmployee(response.data);
            setError(null);
        } catch (err) {
            setError('ไม่สามารถโหลดข้อมูลพนักงานได้');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div className="loading">กำลังโหลด...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!employee) return <div>ไม่พบข้อมูลพนักงาน</div>;

    return (
        <div className="detail-container">
            <div className="detail-header">
                <div>
                    <h2>{employee.title} {employee.first_name} {employee.last_name}</h2>
                    <p style={{ color: '#6c757d' }}>รหัส: {employee.employee_code}</p>
                </div>
                <button className="btn btn-secondary" onClick={() => navigate('/employees')}>
                    กลับ
                </button>
            </div>

            <div style={{ marginBottom: '2rem', borderBottom: '1px solid #dee2e6' }}>
                <button
                    style={{
                        padding: '1rem',
                        marginRight: '1rem',
                        background: activeTab === 'personal' ? '#667eea' : 'transparent',
                        color: activeTab === 'personal' ? 'white' : '#667eea',
                        border: 'none',
                        borderBottom: activeTab === 'personal' ? '3px solid #667eea' : 'none',
                        cursor: 'pointer'
                    }}
                    onClick={() => setActiveTab('personal')}
                >
                    ข้อมูลส่วนตัว
                </button>
                <button
                    style={{
                        padding: '1rem',
                        marginRight: '1rem',
                        background: activeTab === 'history' ? '#667eea' : 'transparent',
                        color: activeTab === 'history' ? 'white' : '#667eea',
                        border: 'none',
                        borderBottom: activeTab === 'history' ? '3px solid #667eea' : 'none',
                        cursor: 'pointer'
                    }}
                    onClick={() => setActiveTab('history')}
                >
                    ประวัติการทำงาน
                </button>
                <button
                    style={{
                        padding: '1rem',
                        marginRight: '1rem',
                        background: activeTab === 'training' ? '#667eea' : 'transparent',
                        color: activeTab === 'training' ? 'white' : '#667eea',
                        border: 'none',
                        borderBottom: activeTab === 'training' ? '3px solid #667eea' : 'none',
                        cursor: 'pointer'
                    }}
                    onClick={() => setActiveTab('training')}
                >
                    การฝึกอบรม
                </button>
                <button
                    style={{
                        padding: '1rem',
                        background: activeTab === 'benefits' ? '#667eea' : 'transparent',
                        color: activeTab === 'benefits' ? 'white' : '#667eea',
                        border: 'none',
                        borderBottom: activeTab === 'benefits' ? '3px solid #667eea' : 'none',
                        cursor: 'pointer'
                    }}
                    onClick={() => setActiveTab('benefits')}
                >
                    สวัสดิการ
                </button>
            </div>

            {activeTab === 'personal' && (
                <div>
                    <div className="detail-section">
                        <h3>ข้อมูลทั่วไป</h3>
                        <div className="detail-grid">
                            <div className="detail-item">
                                <label>ชื่อภาษาไทย</label>
                                <p>{employee.title} {employee.first_name} {employee.last_name}</p>
                            </div>
                            <div className="detail-item">
                                <label>ชื่อภาษาอังกฤษ</label>
                                <p>{employee.first_name_en} {employee.last_name_en}</p>
                            </div>
                            <div className="detail-item">
                                <label>เลขบัตรประชาชน</label>
                                <p>{employee.national_id || '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>วันเกิด</label>
                                <p>{employee.birth_date || '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>เพศ</label>
                                <p>{employee.gender || '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>อีเมล</label>
                                <p>{employee.email || '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>โทรศัพท์</label>
                                <p>{employee.phone || '-'}</p>
                            </div>
                        </div>
                    </div>

                    <div className="detail-section">
                        <h3>ข้อมูลการทำงาน</h3>
                        <div className="detail-grid">
                            <div className="detail-item">
                                <label>แผนก</label>
                                <p>{employee.department_name || '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>ตำแหน่ง</label>
                                <p>{employee.position_name || '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>วันที่เริ่มงาน</label>
                                <p>{employee.hire_date || '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>เงินเดือน</label>
                                <p>{employee.salary ? `฿${parseFloat(employee.salary).toLocaleString()}` : '-'}</p>
                            </div>
                            <div className="detail-item">
                                <label>สถานะการจ้าง</label>
                                <p>{employee.employment_status}</p>
                            </div>
                        </div>
                    </div>

                    <div className="detail-section">
                        <h3>ที่อยู่</h3>
                        {employee.addresses && employee.addresses.length > 0 ? (
                            employee.addresses.map(addr => (
                                <div key={addr.address_id} style={{ marginBottom: '1rem' }}>
                                    <label>{addr.address_type}</label>
                                    <p>
                                        {addr.address_line1} {addr.address_line2}<br />
                                        {addr.sub_district} {addr.district} {addr.province} {addr.postal_code}
                                    </p>
                                </div>
                            ))
                        ) : (
                            <p>ไม่มีข้อมูลที่อยู่</p>
                        )}
                    </div>
                </div>
            )}

            {activeTab === 'history' && (
                <div className="detail-section">
                    <h3>ประวัติการเปลี่ยนแปลง</h3>
                    {employee.history && employee.history.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>วันที่</th>
                                    <th>ประเภท</th>
                                    <th>รายละเอียด</th>
                                    <th>เหตุผล</th>
                                </tr>
                            </thead>
                            <tbody>
                                {employee.history.map(h => (
                                    <tr key={h.history_id}>
                                        <td>{h.effective_date}</td>
                                        <td>{h.change_type}</td>
                                        <td>
                                            {h.change_type === 'Promotion' && `${h.previous_position_name} → ${h.new_position_name}`}
                                            {h.change_type === 'Transfer' && `${h.previous_department_name} → ${h.new_department_name}`}
                                            {h.change_type === 'Salary_Adjustment' && `฿${parseFloat(h.previous_salary).toLocaleString()} → ฿${parseFloat(h.new_salary).toLocaleString()}`}
                                        </td>
                                        <td>{h.reason || '-'}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>ไม่มีประวัติการเปลี่ยนแปลง</p>
                    )}
                </div>
            )}

            {activeTab === 'training' && (
                <div className="detail-section">
                    <h3>การฝึกอบรม</h3>
                    {employee.training && employee.training.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>หลักสูตร</th>
                                    <th>ผู้ให้บริการ</th>
                                    <th>วันที่อบรม</th>
                                    <th>วันที่สำเร็จ</th>
                                    <th>สถานะ</th>
                                </tr>
                            </thead>
                            <tbody>
                                {employee.training.map(t => (
                                    <tr key={t.training_id}>
                                        <td>{t.training_name}</td>
                                        <td>{t.training_provider || '-'}</td>
                                        <td>{t.training_date || '-'}</td>
                                        <td>{t.completion_date || '-'}</td>
                                        <td>{t.status}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>ไม่มีข้อมูลการฝึกอบรม</p>
                    )}
                </div>
            )}

            {activeTab === 'benefits' && (
                <div className="detail-section">
                    <h3>สวัสดิการ</h3>
                    {employee.benefits && employee.benefits.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>ประเภท</th>
                                    <th>รายละเอียด</th>
                                    <th>วันที่เริ่ม</th>
                                    <th>วันที่สิ้นสุด</th>
                                    <th>จำนวน</th>
                                    <th>สถานะ</th>
                                </tr>
                            </thead>
                            <tbody>
                                {employee.benefits.map(b => (
                                    <tr key={b.benefit_id}>
                                        <td>{b.benefit_type}</td>
                                        <td>{b.benefit_description || '-'}</td>
                                        <td>{b.start_date || '-'}</td>
                                        <td>{b.end_date || '-'}</td>
                                        <td>{b.amount ? `฿${parseFloat(b.amount).toLocaleString()}` : '-'}</td>
                                        <td>{b.status}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>ไม่มีข้อมูลสวัสดิการ</p>
                    )}
                </div>
            )}
        </div>
    );
}

export default EmployeeDetail;
